package concrete.data.chatroom;

import common.ICRMessageType;

/**
 * data class to hold text message
 */
public class TextMessageData implements ICRMessageType {

	/**
	 * auto generated UID
	 */
	private static final long serialVersionUID = -609900561005360990L;
	
	/**
	 * text data
	 */
	private String text;

	/**
	 * constructor
	 * @param text data
	 */
	public TextMessageData(String text) {
		this.text = text;
	}
	
	/**
	 * get text data
	 * @return text
	 */
	public String getText() {
		return this.text;
	}

}
